import React, { useState } from 'react'

import { IonPage, IonCard, IonHeader, IonToolbar, IonBackButton, IonContent, IonTitle, IonButtons, IonTextarea,
    IonButton ,IonCardContent, IonLabel, IonItem, IonInput, IonFab, IonFabButton, IonIcon, IonImg} from '@ionic/react'

import { add, save } from 'ionicons/icons'

//------
import { useCamera, availableFeatures } from '@ionic/react-hooks/camera';
import { CameraResultType } from '@capacitor/core'

import { dynamicData } from '../components/frontend/create-table'
import { beansDataFileName, beansSettingsFileName } from '../components/objects/filenames'
import { beansDefault } from '../components/objects/default-values'
import { readSettings, readData, createTable } from '../components/backend/data'

export const ShowBeans = () =>{
    //------------------
    const { photo, getPhoto} = useCamera();
    const handleTakePhoto = () => {
    if(availableFeatures.getPhoto) {
      getPhoto({
        quality: 100,
        allowEditing: false,
        resultType: CameraResultType.DataUrl
      })
    }
}
// -------------------

const [data, setData] = useState()


const fileName = beansSettingsFileName

let fields = []
const fileSettingsRead = async () => {
    //let fields = []
    let fileContent =  await readSettings(fileName)
    console.log('filecontent:', fileContent)
    if (fileContent) {   //if filename doesn't exist readSettings will return false    
        fields = JSON.parse(fileContent)
       // console.log('fields:', fields)
    } else {
        fields = beansDefault
    }
}   
fileSettingsRead()

/*
let savedData = []
let header = []
let fileExists = false
const readSavedData = async () => {
    let fileContent = await readData(beansDataFileName)
    console.log('fileContent isch:', fileContent)
    if (fileContent){
        [header, savedData] = createTable(fileContent)
        console.log('savedData:', savedData)
        fileExists = true
        console.log('file isch do:', fileExists)
    } else {
        //fileExists = false
    }
}
*/
    const readSavedData = () => {
        let savedData = []
        let header = []
        let fileExists = false
        const read = async () =>{
            let fileContent = await readData(beansDataFileName)
            console.log('fileContent isch:', fileContent)
            if (fileContent){
                [header, savedData] = createTable(fileContent)
                console.log('savedData:', savedData)
                fileExists = true
                console.log('file isch do:', fileExists)
            }

            //fileExists = false
        }
        read()
            return savedData
    }

setData( readSavedData())



    return(
        <IonPage>
            <IonHeader>
                <IonToolbar>
                        <IonButtons slot='start'>
                           <IonBackButton defaultHref='home'/>
                        </IonButtons>
                <IonTitle>Bohnen anzeigen</IonTitle>
                </IonToolbar>
                </IonHeader>
            <IonContent>
                <IonFab vertical='top' horizontal='center' slot='fixed'>
                    <IonFabButton color='dark' routerLink='./Add-beans'>
                        <IonIcon icon={add} />
                    </IonFabButton>
                </IonFab>


            <IonButton onClick={() => handleTakePhoto()}>Click</IonButton>
            <IonItem> au Test

                { data && dynamicData(data, fields, beansDataFileName)}


            </IonItem> 

            </IonContent>
        </IonPage>
    )
}
