import React, { useState } from 'react'

import { IonPage, IonCard, IonHeader, IonToolbar, IonBackButton, IonContent, IonTitle, IonButtons, IonTextarea,
    IonButton ,IonCardContent, IonLabel, IonItem, IonInput, IonFab, IonFabButton, IonIcon, IonImg} from '@ionic/react'

import { add, save } from 'ionicons/icons'

//------
import { useCamera, availableFeatures } from '@ionic/react-hooks/camera';
import { CameraResultType } from '@capacitor/core'

import { dynamicData } from '../components/frontend/create-table'
import { beansDataFileName, beansSettingsFileName } from '../components/objects/filenames'
import { beansDefault } from '../components/objects/default-values'
import { readSettings, readData } from '../components/backend/data'



export const ShowBeans = () => {

    const [data, setData] = useState([])
   // const [tableData, setTableData] = useState([])

   // !!! add check if file exists !!! 

   // let data = []
    const readFileData = async () => {
        setData( await readData(beansDataFileName))
        console.log('data:',data)
    }
    readFileData()



    let fields = []
    const fileSettingsRead = async () => {
        //let fields = []
        let fileContent =  await readSettings(beansSettingsFileName)
        console.log('filecontent:', fileContent)
        if (fileContent) {   //if filename doesn't exist readSettings will return false    
            fields = JSON.parse(fileContent)
        // console.log('fields:', fields)
        } else {
            fields = beansDefault
        }
    }   
    fileSettingsRead()




    return (
        <IonPage>
        <IonContent>
            <IonItem>TEST</IonItem>
            <IonItem>{data}</IonItem>
            <IonItem>{data !== [] && dynamicData(data,fields,beansDataFileName)}</IonItem>
        </IonContent>
        </IonPage>
    )
}
