import React from 'react'
import { IonItem, IonReorder, IonReorderGroup, IonItemDivider, IonCheckbox,
    IonLabel, IonContent, IonButton, IonIcon, IonButtons, IonInput } from '@ionic/react'

import { trash, save, close, addCircle } from 'ionicons/icons'

const fields = [
    ['Bohne', true],
    ['RÃ¶ster', true],
    ['Kommentar', false]
]

let listOfItems = [0, 1, 2]

// copied from the documentation:
function doReorder(items, event) {
    // The `from` and `to` properties contain the index of the item
    // when the drag started and ended, respectively
    console.log('Dragged from index', event.detail.from, 'to', event.detail.to);
    // Finish the reorder and position the item in the DOM based on
    // where the gesture ended. This method can also be called directly
    // by the reorder group
    //event.detail.complete();
    const newIndex = event.detail.to
    const oldIndex = event.detail.from
    let newItems = [...items]

    if(oldIndex < newIndex){
        newItems.splice(event.detail.to, 0, newItems[event.detail.from])
        newItems.splice(oldIndex, 1)
    } else if (oldIndex > newIndex) {
        newItems.splice(event.detail.to, 0, newItems[event.detail.from])
        newItems.splice(oldIndex + 1, 1)
    } 

    event.detail.complete(true);
    listOfItems = newItems
    console.log(items)
}

const displayItems = () => {

    //fields needs to be read from file and handed over to function
    const result = fields.map((arrayItem, index) => 
        <IonItem>
            <IonCheckbox slot='start' color='light' checked={arrayItem[1]}
                    name={index}
                    onIonChange={ e => console.log(e.target.name) }/>
            <IonLabel>{arrayItem[0]}, {index}</IonLabel>
            <IonButton color='light'
                        name={index}
                        onClick={ e => console.log(e.target.name)}
                        > <IonIcon icon={trash}/></IonButton>
            <IonReorder slot='end'/>
        </IonItem>
        )
    return result
}

export const sortItems = () => {

    return (
        <IonContent>
            <IonItem>
                <IonLabel slot='start'>Show</IonLabel>
                <IonLabel slot='end'>Reorder</IonLabel>
            </IonItem>

            <IonReorderGroup disabled={false}  onIonItemReorder={ (e) => doReorder(listOfItems, e)}>
                {displayItems()}
            </IonReorderGroup>

        <IonItem >
            <IonInput background='light' placeholder='Feld hinzufÃ¼gen'></IonInput>
            <IonButton color='light'
                        shape='round'
                        size='large'>
                <IonIcon slot='icon-only' icon={addCircle}/></IonButton>
                <IonLabel><br/>Feld hinzufÃ¼gen</IonLabel>
                <IonItem>
                <IonIcon slot='icon-only' icon={addCircle} onClick={console.log('you clicked me')}/> </IonItem>
        </IonItem>

        <IonItem class='ion-padding'>
            <IonButton color='light'
                    class="ion-text-capitalize"
                    shape='round'
                    size='default'
                    ><IonIcon icon={save} slot='start'/>speichern</IonButton>          
            <IonButton color='light'
                    class="ion-text-capitalize"
                    shape='round'
                    size='default'

                    ><IonIcon icon={close} slot='start'/>Abbrechen</IonButton>
        </IonItem>
        </IonContent>


    )
}
