export const idCreator = () => {
    let date = new Date()
    let time = date.getTime()
    return time
}
console.log('id:',idCreator())

export const dateCreator = () =>{
    const date = new Date()
    let day = date.getDay()
    let month = date.getMonth()+1 //month start from 0
    let year = date.getFullYear()
    return toString(day)+"."+toString(month)+"."+toString(year)
}
console.log('date:', dateCreator())

export const timeCreator = () => {
    const date = new Date()
    const minute = date.getMinutes()
    const hour = date.getHours()
    return toString(hour).concat(":",toString(minute))
} 

console.log('time:', timeCreator())
