import React from 'react'
import { useState, useEffect } from 'react'

import { IonPage, IonCard, IonHeader, IonToolbar, IonBackButton, IonContent, IonTitle, IonButtons, IonTextarea,
    IonCardContent, IonLabel, IonItem, IonInput, IonGrid, IonRow, IonCol, IonIcon, IonButton, IonImg} from '@ionic/react'

import { trash, save, camera, cameraReverse} from 'ionicons/icons'

import { beansDataFileName } from '../objects/filenames'
import { storeData } from '../backend/data'

import { useCamera, availableFeatures } from '@ionic/react-hooks/camera';
import { CameraResultType } from '@capacitor/core'

//import './add-items.css'


export const AddItems = (defaultSetttings, settingsFileName, fileName) => {

    const [settings, setSettings] = useState([])
    const [isFileRead, setIsFileRead] = useState([false])



const fileSettingsRead = async () => {
    setIsFileRead(true)
    let fields = []
    let fileContent =  await readSettings(settingsFileName)
    console.log('filecontent:', fileContent)
    if (fileContent) {   //if filename doesn't exist readSettings will return false    
        fields = JSON.parse(fileContent)
       // console.log('fields:', fields)
    } else {
        fields = defaultSetttings
    }
    setSettings(fields)

}   

if(isFileRead === false) {fileSettingsRead()}


    const [newArray, setNewArray] = useState(settings.map((arrayItem, index) => arrayItem.concat([index, ''])))
    //let newArray = array.map((arrayItem, index) => arrayItem.concat([index, '']))
    // example of arrray ['Bean', true, 0, '']
    // => Bean is the field title; true if it should be displayed; 0 is the index; '' will be filled with content eg. Malabar 
    console.log(newArray)

    const [inputValues, setInputValues] = useState(JSON.parse(JSON.stringify(newArray)) )






    let { photo, getPhoto } = useCamera();
    const  [ myPhoto, setMyPhoto ] = useState(photo)

    const handleTakePhoto = async () => {
        if(availableFeatures.getPhoto) {
         await getPhoto({
            quality: 100,
            allowEditing: false,
            resultType: CameraResultType.DataUrl,
            height: 1000
            })
        //console.log('inputvalues:', inputValues)
        //setNewArray(inputValues.map( (arrayItem, index) => arrayItem[3] = inputValues[index][3]))  That's where I stopped
        //setMyPhoto(photo)
        //console.log('photo', photo)
        }
    }

    useEffect(() => setMyPhoto(photo), [photo])

    const delFunc = () => {
        //console.log('newarray1:',newArray)
        setInputValues(inputValues.map(arrayItem => arrayItem.splice(0,3).concat([''])))
        console.log('inputvalueSSS:', inputValues)
        //setNewArray()
        setMyPhoto(undefined)
        //console.log('newarray2:',newArray)
    }

    const saveIt = async (fileName, array, photo) => {

        // https://stackoverflow.com/questions/42974735/create-object-from-array
        const objectToSave = array.reduce(( o, arrayItem) => ({...o, [arrayItem[0]]:arrayItem[3] }) , {})
        await storeData(fileName, objectToSave, photo)
        delFunc()
    }

    //debugger
    console.log('newarray1:',newArray)
    const result = inputValues.filter(arrayItem => arrayItem[1])
                        .map((arrayItem) => 
        <IonItem>
            <IonInput placeholder={arrayItem[0]}
                name={arrayItem[0]}
                id={arrayItem[2]}
                value={arrayItem[3]}
                onIonChange={ e => {inputValues[e.target.id][3]=e.detail.value; setInputValues(inputValues); console.log(inputValues)}}
            ></IonInput>
        </IonItem>  
        )
    return(<>
    <IonGrid>
        <IonRow>
            <IonCol>
            { myPhoto && <IonImg height='200px' src={myPhoto.dataUrl} />}
            </IonCol>
        </IonRow>
        <IonRow>
            <IonCol>
            <IonButton color='dark'
                onClick={() => handleTakePhoto()}>
                <IonIcon slot='icon-only' src={myPhoto ? cameraReverse : camera}/>
            </IonButton>
            </IonCol>
        </IonRow>
    </IonGrid>
    {result}
    <IonGrid>
        <IonRow>
            <IonCol>
                <IonButton color='dark'
                        routerLink='./Show-beans'
                        onClick={() => saveIt(fileName, inputValues, myPhoto)} >
                    <IonIcon icon={save} slot='icon-only'/>
                </IonButton>
            </IonCol>
            <IonCol>
            <IonButton color='dark'
                onClick={ () => delFunc()}>
                    <IonIcon icon={trash} slot='icon-only'/>
                </IonButton>
            </IonCol>
        </IonRow>
    </IonGrid>

    </>
    )
}
