import React, { useState, useEffect } from 'react'

import { IonPage, IonCard, IonCardHeader, IonCardTitle, IonHeader, IonToolbar, IonBackButton, IonContent, IonTitle, IonButtons, IonTextarea,
    IonButton ,IonCardContent, IonLabel, IonItem, IonInput, IonFab, IonFabButton, IonIcon, IonImg, IonList} from '@ionic/react'

import { add, save, newspaper } from 'ionicons/icons'

//------
import { useCamera, availableFeatures } from '@ionic/react-hooks/camera';
import { CameraResultType } from '@capacitor/core'

import { picData, createTable } from '../components/frontend/create-table'
import { beansDataFileName, beansSettingsFileName } from '../components/objects/filenames'
import { beansDefault } from '../components/objects/default-values'
import { readSettings, readData, readPicture } from '../components/backend/data'


const settingsFileName = beansSettingsFileName
const settingsDefault = beansDefault
const dataFileName = beansDataFileName

export const ShowBeans = () => {

    const [data, setData] = useState([])
    const [picArray, setPicArray] = useState([])


    let settings = []
    useEffect(()=>{
        const loadAllFileData = async () => {
            //load Settings
            let fileContent =  await readSettings(settingsFileName)
            console.log('filecontent:', fileContent)
            if (fileContent) {   //if filename doesn't exist readSettings will return false    
                settings = JSON.parse(fileContent)
            } else {
                settings = settingsDefault
            }
            // load data
            const rawData = await readData(dataFileName)
            const tempData = await createTable(rawData)
            console.log('tempdata type:', typeof tempData)
            setData(tempData)

            const folder = dataFileName.split('/')[0]
            /*
            for (let i=0; i<tempData.length; i++){
                const temp = await readPicture(folder + '/' + tempData[i].id.toString() + '.jpeg')
                console.log('temp',temp)
                picArray.push(temp)
            }
            setPicArray(picArray) */
            let tempArray = []
            let tempPics = {}
            for (let i=0; i<tempData.length; i++){
                const temp = await readPicture(folder + '/' + tempData[i].id.toString() + '.jpeg')
                tempPics[tempData[i].id]=temp
                console.log('tempData von i',tempData[i])
                console.log('tempPics:', tempPics)
                //console.log('temp',temp)
                //picArray.push(temp)
            }
            tempArray.push(tempPics)
            console.log('tempArray:', tempArray)
            setPicArray(tempArray)




            //const tempPics = await picData(tempData, dataFileName)
            //setPicArray(tempPics)

            //

        }
        loadAllFileData()
    }, [])




//const newArray = [...picArray]
let res2
if(picArray.length > 0 && data.length > 0){
    res2 = data.reverse().map((row, index) => 

        <IonCard>
            <IonCardHeader>
                <IonCardTitle></IonCardTitle>
            </IonCardHeader>
            <IonCardContent>
                <IonLabel>{Object.keys(row)[1]}: {row[Object.keys(row)]}</IonLabel>
            </IonCardContent>
        </IonCard>

    )
}



