import React from 'react'
import { IonItem, IonReorder, IonReorderGroup, IonItemDivider, IonCheckbox, IonLabel, IonContent } from '@ionic/react'

const fields = {
    Bohnen: true,
    Röster: true,
    Kommentar: true
}

let items = [1, 2, 3]

// copied froom the documentation:
function doReorder(items, event) {
    // The `from` and `to` properties contain the index of the item
    // when the drag started and ended, respectively
    console.log('Dragged from index', event.detail.from, 'to', event.detail.to);
    // Finish the reorder and position the item in the DOM based on
    // where the gesture ended. This method can also be called directly
    // by the reorder group
    event.detail.complete(items);
    console.log(items)
}

export const sortItems = () => {

    return (
        <IonContent>
                {/*-- Default reorder icon, end aligned items --*/}
        <IonItem>Show</IonItem>
        <IonItem>Reorder</IonItem>
        <IonReorderGroup disabled={false}  onIonItemReorder={ (e) => doReorder(items, e)}>
            <IonItem>
            <IonReorder slot='end'/>
            <IonLabel>Bohnen</IonLabel>

            </IonItem>



            <IonItem>
            <IonReorder slot='end'/>
            <IonLabel>Röster</IonLabel>

            </IonItem>



            <IonItem>
            <IonReorder slot='end'/>
            <IonLabel>Kommentar</IonLabel>

            </IonItem>



        </IonReorderGroup>




        </IonContent>





    )
}
