import React, { useState } from 'react'
import { RouteComponentProps } from 'react-router'

import { IonButton, IonPopover, IonItem, IonIcon, IonCheckbox, IonButtons, IonBackButton } from "@ionic/react"

import { ellipsisHorizontalOutline, ellipsisVerticalOutline  } from 'ionicons/icons'


export const Menu = (props) =>  {

    const [showPopover, setShowPopover] = useState(false)

    return (
    <div>

        <IonPopover
        isOpen={showPopover}
        onDidDismiss={e => setShowPopover(false)}
        animated={true}
        showBackdrop={true}
        backdropDismiss={true}

      > 
        <IonButtons>
          <IonBackButton/>
        </IonButtons>
        <IonItem><IonCheckbox slot='end' color='light'></IonCheckbox>Sprachsteuerung</IonItem>
        <IonItem>Einstellungen Br√ºhungen</IonItem>
        <IonItem routerLink='./Beans-settings'>Einstellungen Bohnen</IonItem>

      </IonPopover>


      <IonButton fill='clear'
                color='dark'
        onClick={() => setShowPopover(true)}>
          <IonIcon slot="icon-only" icon={ellipsisVerticalOutline}></IonIcon>
        </IonButton> 
    </div>
    )
}
