import React from 'react'
import { IonItem, IonReorder, IonReorderGroup, IonItemDivider, IonCheckbox, IonGrid, IonRow, IonCol,
    IonLabel, IonContent, IonButton, IonIcon, IonButtons, IonInput, IonTextarea } from '@ionic/react'

import { trash, save, close, addCircle } from 'ionicons/icons'

import { useState, useReducer } from 'react'
import { delimiter } from 'path'

import { readSettings, overWriteFile } from '../backend/data'
import { read } from 'fs'


const fields = [
    ['Bohne', true],
    ['Röster', true],
    ['Kommentar', false]]

const fileName = 'data/beans-settings.data'

let isFileRead = false

export const SortItems = () => {

    const [ignored, forceUpdate] = useReducer(x => x + 1, 0) // this function will force an updated of the dom
    const [items, setItems] = useState(fields)
    // const [tempItems, setTempItems] = useState(['banane'])
     const [tempItem, setTempItem] = useState('')

     const fileRead = async () => {
        if (!isFileRead){
            let fileContent = await readSettings(fileName)
            if (fileContent) {   //if filename doesn't exist readSettings will return false    
            setItems(JSON.parse(fileContent))
            isFileRead = true
            } else {
                setItems(fields)
            }
        }
    }

    fileRead()  //will be alway loaded the page is opened, or on every rerender? Dangerous if rerender!

    const fileSave = async (fileName, object) => {
        await overWriteFile(fileName, object)
    }




    const addItem = (newItem, arrayOfItems) => {
        //const tempVar = items
        arrayOfItems.push([newItem, true])
        setItems(arrayOfItems)
        setTempItem('')
        console.log(items)

    }

/*
const reorder = (oldIndex, newIndex, arrayToOrder) => {

    //let newItems = [...arrayToOrder]
    let newItems = Array.from(arrayToOrder)  //copies the array all levels deep (not shallow)
    console.log(items)
    console.log('arrayToOrder')
   // console.log(arrayToOrder)
    console.log('new Items')
    console.log(newItems)


   // debugger
    if(oldIndex < newIndex){
        newItems.splice(newIndex+1, 0, newItems[oldIndex])
            console.log('splice start')
            console.log(newItems)
        newItems.splice(oldIndex, 1)
            console.log(newItems)
            console.log('splice finished')
    } else if (oldIndex > newIndex) {
        newItems.splice(newIndex, 0, newItems[oldIndex])
            console.log('splice start')
            console.log(newItems)
        newItems.splice(oldIndex + 1, 1)
            console.log(newItems)
            console.log('splice finished')
    } 

    setTempItems(newItems)
    console.log('TemPITEMS')
    console.log(tempItems)
    //debugger   
    console.log(newItems)
} */

// copied from the documentation:
    function doReorder(itemsSort, event) {

        // The `from` and `to` properties contain the index of the item
        // when the drag started and ended, respectively
        console.log('Dragged from index', event.detail.from, 'to', event.detail.to);
        // Finish the reorder and position the item in the DOM based on
        // where the gesture ended. This method can also be called directly
        // by the reorder group
        //event.detail.complete();
        //event.detail.complete(true);
        const newIndex = event.detail.to
        const oldIndex = event.detail.from
        //let newItems = [...itemsSort]

        console.log('before:', itemsSort)

        itemsSort = event.detail.complete(itemsSort);

        console.log('after:', itemsSort)
        setItems(itemsSort)
        //reorder(oldIndex, newIndex, itemsSort)
    }
    const delItem = (index, arrayToDeleteFrom) => {
        arrayToDeleteFrom.splice(index, 1)
        setItems(arrayToDeleteFrom)
        forceUpdate()
    }

    const checkBox = (index, state, arrayToCheck) => {
        arrayToCheck[index][1]=state
        setItems(arrayToCheck)
        console.log(items)
    }


    const displayItems = (itemsToDisplay) => {

        //fields needs to be read from file and handed over to function
    const result = itemsToDisplay.map((arrayItem, index) => 
        <IonItem>
            <IonCheckbox slot='start' color='light' checked={arrayItem[1]}
                    name={index}
                    onIonChange={ e => checkBox(e.target.name, e.detail.checked, items) }/>
            <IonLabel>{arrayItem[0]}</IonLabel>
            <IonButton color='light'
                        name={index}
                        onClick={ e => delItem(e.target.name, items)}
                        > <IonIcon icon={trash}/></IonButton>
            <IonReorder slot='end'/>
        </IonItem>
        )
    return result
}


    return (
        <IonContent>
            <IonItem>
                <IonLabel slot='start'>Anzeigen</IonLabel>
                <IonLabel slot='end'>Reihenfolge</IonLabel>
            </IonItem>

            <IonReorderGroup disabled={false}  onIonItemReorder={ (e) => doReorder(items, e)}>
                {displayItems(items)}
            </IonReorderGroup>

        <IonItem class='ion-padding'>
            <IonInput background='light'
                    placeholder='Feld hinzufügen'
                    onIonChange={e => setTempItem(e.detail.value)} value={tempItem}></IonInput>
            <IonButton color='light'  
                    onClick={() => addItem(tempItem,items)}>
                    <IonIcon slot='icon-only' icon={addCircle} size='default' color='dark'/>
                    </IonButton>
        </IonItem>

            <IonGrid><IonRow><IonCol>
            <IonButton color='light'
                    class="ion-text-capitalize"
                    shape='round'
                    size='default'
                    routerLink='./Home'
                    onClick={() => fileSave(fileName, items)}
                    ><IonIcon icon={save} slot='start'size='large'/>Speichern</IonButton>
            </IonCol>
            <IonCol>          
            <IonButton color='light'
                    class="ion-text-capitalize"
                    shape='round'asdfa
                    routerLink='./Home'
                    onClick={isFileRead = false}
                    ><IonIcon icon={close} slot='start' size='large'/>Abbrechen</IonButton>
            </IonCol></IonRow></IonGrid>
        </IonContent>


    )
}
