import React, { useEffect, useLayoutEffect, useContext, useState, useReducer} from 'react'
import { IonItem, IonReorder, IonReorderGroup, IonItemDivider, IonCheckbox, IonGrid, IonRow, IonCol,
    IonLabel, IonContent, IonButton, IonIcon, IonButtons, IonInput, IonTextarea } from '@ionic/react'

import { trash, save, close, addCircle } from 'ionicons/icons'

import { readSettings, overWriteFile } from '../backend/data'
import { store } from '../backend/store'
import { stat } from 'fs'

// fields is the basic array of fields that's available upon installation
/*
const fields = [
    ['Bohne', true],
    ['Röster', true],
    ['Kommentar', false]]

const fileName = 'data/beans-settings.data'
*/


export const SortItems = (fileName, defaultSettings) => {
    //ReactFunc()
    const globalState = useContext(store)
    const { state, dispatch } = globalState

    const [ignored, forceUpdate] = useReducer(x => x + 1, 0) // this function will force an updated of the dom
 //   const [items, setItems] = useState(JSON.parse(JSON.stringify(state.beans.settings)))
    const [tempItems, setTempItems] = useState(JSON.parse(JSON.stringify(state.beans.settings)))
    const [tempItem, setTempItem] = useState('')
    const [isFileRead, setIsFileRead] = useState(false)

    const reducer = (myState, action) => {
        console.log('REDUCERxxxxxx:', myState, action)
        let tempArray = JSON.parse(JSON.stringify(myState))
        switch (action.type){
            case 'swap':
                return action.value
            case 'addRow':
                tempArray.push([action.itemName, true])
                console.log('TEMPARRAY in addROW', tempArray)
                return tempArray
            case 'delRow':
                console.log('DELROW:', myState, action.index)

                //tempArray = JSON.parse(JSON.stringify(state))
                tempArray.splice(action.index, 1)
                return tempArray
                //return state.splice(action.index, 1)
            case 'check':
                tempArray[action.index][1] = action.checkState
                return tempArray
                //return state[action.index][1] = action.checkState
            case 'reset':
                return JSON.parse(JSON.stringify(state.beans.settings))
            //case 'test':
              //  return console.log('------------ HEYHo***************') 
            default:
                throw new Error()

        }
    }


    const [items, setItems] = useReducer(reducer, JSON.parse(JSON.stringify(state.beans.settings)))
    console.log('ITEMSS*******************', items )

    const fileSave = async (fileName, object) => {
        await overWriteFile(fileName, object)
        dispatch({type: 'setBeansSettings', value: object})
        //setIsFileRead(false)
    }

    const addItem = (newItem, arrayOfItems) => {
        //const tempVar = items

        //arrayOfItems.push([newItem, true])
        //setItems(arrayOfItems)
        setItems({type: 'addRow', itemName: newItem})
        setTempItem('')
        console.log(items)

    }


// copied from the documentation:
    function doReorder(itemsSort, event) {

        // The `from` and `to` properties contain the index of the item
        // when the drag started and ended, respectively
        console.log('Dragged from index', event.detail.from, 'to', event.detail.to);
        // Finish the reorder and position the item in the DOM based on
        // where the gesture ended. This method can also be called directly
        // by the reorder group
        //event.detail.complete();
        //event.detail.complete(true);
        const newIndex = event.detail.to
        const oldIndex = event.detail.from
        //let newItems = [...itemsSort]

        itemsSort = event.detail.complete(itemsSort)

        //setItems(itemsSort)
        setItems({type: 'swap', value: itemsSort})
       //setItems({type:'test'})
     //   console.log('tempits:',tempItems)
    }
    const delItem = (index, arrayToDeleteFrom) => {

        //arrayToDeleteFrom.splice(index, 1)
        //setItems(arrayToDeleteFrom)
        setItems({type:'delRow', index:index})

        forceUpdate()
    }

    const checkBox = (index, checkState, arrayToCheck) => {

        // arrayToCheck[index][1]=checkState
        // setItems(arrayToCheck)
        setItems({type:'check', index: index,    checkState: checkState})
        // return state[action.index][1] = action.checkState

        console.log('Checkbox',items)
    }


    const displayItems = (itemsToDisplay) => {
        const result = itemsToDisplay.map((arrayItem, index) => 
            <IonItem> {console.log('Arrayitem,index', arrayItem, index)}
                <IonCheckbox slot='start' color='dark' checked={arrayItem[1]}
                        name={index}
                        onIonChange={ e => {console.log('in map checkbox:', e.target.name); checkBox(e.target.name, e.detail.checked, JSON.parse(JSON.stringify(items)) )} }/>
                <IonLabel>{arrayItem[0]}</IonLabel>
                <IonButton color='dark'
                            name={index}
                            onClick={ e => delItem(e.target.name, JSON.parse(JSON.stringify(items)) )}
                            > <IonIcon icon={trash}/></IonButton>
                <IonReorder slot='end'/>
            </IonItem>
            )
    return result
    }
    console.log('itemsbefore display', items)
    const toDisplay = displayItems(items)

    return (
        <IonContent>
            <IonItem>
                <IonLabel slot='start'>Anzeigen</IonLabel>
                <IonLabel slot='end'>Reihenfolge</IonLabel>
            </IonItem>

            <IonReorderGroup disabled={false}  onIonItemReorder={ (e) => doReorder(items, e)}>
                {toDisplay}
            </IonReorderGroup>

        <IonItem class='ion-padding'>
            <IonInput background='dark'
                    placeholder='Feld hinzufügen'
                    onIonChange={e => setTempItem(e.detail.value)} value={tempItem}></IonInput>
            <IonButton color='dark' style={{height: '35px', width: '35px'}} 
                    onClick={() => addItem(tempItem,items)}>
                    <IonIcon icon={addCircle} style={{fontSize: '50px'}}  />
                    </IonButton>
        </IonItem>

            <IonGrid><IonRow><IonCol>
            {/* Save */}
            <IonButton color='dark'
                    class="ion-text-capitalize"
                    size='default'
                    routerLink='./Home'
                    onClick={() => {fileSave(fileName, items); /*setCounter(counter+1)*/; /*setTempItems(items)*/; }}
                    ><IonIcon icon={save} slot='start'size='large'/>Speichern</IonButton>
            </IonCol>
            <IonCol>
            {/* Cancel */}          
            <IonButton color='dark'
                    class="ion-text-capitalize"
                    size='default'

                    onClick={() => setItems({type:'reset'})}
                    ><IonIcon icon={close} slot='start' size='large'/>Abbrechen</IonButton>
            </IonCol></IonRow></IonGrid>  {/*routerLink='./Home'*/}
        </IonContent>

    ) 
}
