export const idCreator = () => {
    let date = new Date()
    let time = date.getTime()
    return time
}


export const dateCreator = () =>{
    const date = new Date()
    const day = date.getDay
    const month = date.getMonth+1 //month start from 0
    const year = date.getFullYear
    return toString(day)+"."+toString(month)+"."+toString(year)
}


export const timeCreator = () => {
    const date = new Date()
    const minute = date.getMinutes
    const hour = date.getHours
    return toString(hour)+":"+toString(minute)
}
