import { IonCard, IonCardContent, IonItem, IonImg } from '@ionic/react';
import React from 'react';

import { readPicture } from '../backend/data'
import { read } from 'fs';

export const dynamicData = (array, settings, filename) => {
    //object contains an array with an object inside
    // every row of the object needs to have the same keys => to be deletet?
    //the keys of the object will be the titles

    const titles = Object.keys(array[0])

    /* Explanation what the function does:
    1. the outer map iterates through all the objects (lines) in the array
        and creates the same amount of Ioncards.
    2. The variable object contains each of the single objects in array
    3: Object.keys(object) creates an array with all the keys in object
    4. .filter removes the key with id
    5. map iterates through all the remaining keys and creates for each key
        a IonCardconten with the content key: value
    */

    folder = filename.split('/')[0]

    const card = array.map(object => {
        const picture = readPicture(folder + '/' + object.id.toString() + '.jpeg');
        if(picture) {
            <IonItem>
                <IonImg src={picture} />
            </IonItem>
        }
    })

