import React, { useState } from 'react'
import { IonButton, IonPopover, IonItem, IonIcon } from "@ionic/react"

import { ellipsisHorizontalOutline, ellipsisVerticalOutline  } from 'ionicons/icons'


export const Menu = () =>  {

    const [showPopover, setShowPopover] = useState(false)

    return (
    <div>

        <IonPopover
        isOpen={showPopover}
        onDidDismiss={e => setShowPopover(false)}
      >
        <p>This is popover content</p>
      </IonPopover>
      <IonButton color='light'
                slot='end'
        onClick={() => setShowPopover(true)}>
          <IonIcon slot="icon-only" ios="ellipsis-horizontal" md="ellipsis-vertical"></IonIcon>
        </IonButton>
    </div>
    )
}
