import React from 'react'
import { useState } from 'react'

import { IonPage, IonCard, IonHeader, IonToolbar, IonBackButton, IonContent, IonTitle, IonButtons, IonTextarea,
    IonCardContent, IonLabel, IonItem, IonInput, IonGrid, IonRow, IonCol, IonIcon, IonButton, IonImg} from '@ionic/react'

import { trash, save} from 'ionicons/icons'

import { beansDataFileName } from '../objects/filenames'
import { storeData } from '../backend/data'

import { useCamera, availableFeatures } from '@ionic/react-hooks/camera';
import { CameraResultType } from '@capacitor/core'

//import './add-items.css'

const saveIt = async (fileName, array, photo) => {

    // https://stackoverflow.com/questions/42974735/create-object-from-array
    const objectToSave = array.reduce(( o, arrayItem) => ({...o, [arrayItem[0]]:arrayItem[3] }) , {})
    await storeData(beansDataFileName, objectToSave, photo)
}

export const AddItems = (array) => {

    const { photo, getPhoto} = useCamera();
    const handleTakePhoto = () => {
        if(availableFeatures.getPhoto) {
        getPhoto({
            quality: 50,
            allowEditing: false,
            resultType: CameraResultType.DataUrl,
            height: 50
            })
        }
    }


    //const [picStore, SetPicStore] = useState()
    //console.log('picstore',picStore)

    let newArray = array.map((arrayItem, index) => arrayItem.concat([index, '']))
    // example of arrray ['Bean', true, 0, '']
    // => Bean is the field title; true if it should be displayed; 0 is the index; '' will be filled with content eg. Malabar 

    const [inputValues, setInputValues] = useState(newArray)
    //debugger
    const result = newArray.filter(arrayItem => arrayItem[1])
                        .map((arrayItem) => 
        <IonItem>
            <IonInput placeholder={arrayItem[0]}
                name={arrayItem[0]}
                id={arrayItem[2]}
                value={arrayItem[3]}
                onIonChange={ e => {inputValues[e.target.id][3]=e.detail.value; setInputValues(inputValues) }}

                ></IonInput>
        </IonItem>  
        )
    return(<>
    <IonItem>
        { photo && <IonImg src={photo.dataUrl} style={{opacity: 0.1}}/>}
    </IonItem>
    {result}
    <IonGrid>
        <IonRow>
            <IonCol>
                <IonButton color='dark'
                        routerLink='./Show-beans'
                        onClick={() => {saveIt(beansDataFileName, inputValues, photo)
                            setInputValues(inputValues.map(arrayItem => arrayItem.splice(0,3).concat([''])))}} >
                    <IonIcon icon={save} slot='icon-only'/>
                </IonButton>
            </IonCol>
            <IonCol>
            <IonButton color='dark'
                onClick={ () => setInputValues(inputValues.map(arrayItem => arrayItem.splice(0,3).concat([''])))}>
                    <IonIcon icon={trash} slot='icon-only'/>
                </IonButton>
            </IonCol>
            <IonCol>
            <IonButton color='dark'
                onClick={() => handleTakePhoto()}>
                    picture 

                </IonButton>
            </IonCol>
        </IonRow>
    </IonGrid>

    </>
    )
}
