//import './My-Style.css'

import React from 'react'
import { IonPage, IonCard, IonHeader, IonToolbar, IonBackButton, IonContent, IonTitle, IonButtons, IonTextarea,
    IonButton ,IonCardContent, IonLabel, IonItem, IonInput} from '@ionic/react'

import { readSettings, readData, createTable} from '../components/backend/data'
import { AddItems } from '../components/frontend/add-items'
import { beansDefault } from '../components/objects/default-values'
import { beansSettingsFileName, beansDataFileName } from '../components/objects/filenames'
import { dynamicData } from '../components/frontend/create-table'


const fileName = beansSettingsFileName

let fields = []
const fileSettingsRead = async () => {
    //let fields = []
    let fileContent =  await readSettings(fileName)
    console.log('filecontent:', fileContent)
    if (fileContent) {   //if filename doesn't exist readSettings will return false    
        fields = JSON.parse(fileContent)
       // console.log('fields:', fields)
    } else {
        fields = beansDefault
    }
}   
let test = fileSettingsRead()


let savedData = []
let header = []
let fileExists = false
const readSavedData = async () => {
    let fileContent = await readData(beansDataFileName)
    if (fileContent){
        [header, savedData] = createTable(fileContent)
        fileExists = true
    } else fileExists = false
}
readSavedData()

export const AddBeans = () => {
    return (
        <IonPage>
            <IonHeader>
                <IonToolbar>
                        <IonButtons slot='start'>
                           <IonBackButton defaultHref='home'/>
                        </IonButtons>
                <IonTitle>Bohne erfassen</IonTitle>
                </IonToolbar>
                </IonHeader>
            <IonContent>
                <IonCard>
                    <IonCardContent>
                        {AddItems(fields)}
                        {window.location.reload(false)}
                    </IonCardContent>
                </IonCard>
                <IonItem></IonItem>

                {fileExists && dynamicData(savedData)}

            </IonContent>
        </IonPage>
    )
}
