if(picArray.length > 0 && data.length > 0){
    res2 = data.reverse().map((row, index) => 

        <IonCard>
            {settings.filter(setting => setting[1] === 0).map(setting =>
            <>
            <IonCardHeader> 
                <IonCardTitle>{setting[0]}</IonCardTitle>
            </IonCardHeader>
            <IonCardContent>
                <IonLabel>{Object.keys(row)[1]}: {row[Object.keys(row)[1]]}</IonLabel>
            </IonCardContent>

            </>)}
        </IonCard>
    )
}

