    return (
        <IonPage>
            <IonHeader>
                <IonToolbar>
                        <IonButtons slot='start'>
                           <IonBackButton defaultHref='home'/>
                        </IonButtons>
                <IonTitle>Bohnen anzeigen</IonTitle>
                </IonToolbar>
                </IonHeader>
            <IonContent>
                <IonFab vertical='top' horizontal='center' slot='fixed'>
                    <IonFabButton color='dark' routerLink='./Add-beans'>
                        <IonIcon icon={add} />
                    </IonFabButton>
                </IonFab>
            <IonList>
                {res2}
            </IonList>
            </IonContent>
        </IonPage>
    )
}
