import React, { useState } from 'react'
import { IonButton, IonPopover, IonItem, IonIcon, IonCheckbox } from "@ionic/react"

import { ellipsisHorizontalOutline, ellipsisVerticalOutline  } from 'ionicons/icons'


export const Menu = () =>  {

    const [showPopover, setShowPopover] = useState(false)

    return (
    <div>

        <IonPopover
        isOpen={showPopover}
        onDidDismiss={e => setShowPopover(false)}
        animated={true}
        showBackdrop={true}

      >
        <IonItem><IonCheckbox slot='end' color='light'></IonCheckbox>Sprachsteuerung</IonItem>
        <IonItem>Einstellungen Br√ºhungen</IonItem>
      </IonPopover>


      <IonButton fill='clear'
                color='dark'
        onClick={() => setShowPopover(true)}>
          <IonIcon slot="icon-only" icon={ellipsisVerticalOutline}></IonIcon>
        </IonButton> 
    </div>
    )
}
