import React, { useState, useEffect } from 'react'

import { IonPage, IonCard, IonCardHeader, IonCardTitle, IonCardSubtitle, IonHeader, IonToolbar, 
    IonBackButton, IonContent, IonTitle, IonButtons, IonTextarea, IonGrid, IonRow, IonCol,
    IonButton ,IonCardContent, IonLabel, IonItem, IonInput, IonFab, IonFabButton, IonIcon, IonImg, IonList, IonNote} from '@ionic/react'

import { add, save, newspaper } from 'ionicons/icons'
import '../../src/'

//------
import { useCamera, availableFeatures } from '@ionic/react-hooks/camera';
import { CameraResultType } from '@capacitor/core'

import { picData, createTable } from '../components/frontend/create-table'
import { beansDataFileName, beansSettingsFileName } from '../components/objects/filenames'
import { beansDefault } from '../components/objects/default-values'
import { readSettings, readData, readPicture } from '../components/backend/data'


const settingsFileName = beansSettingsFileName
const settingsDefault = beansDefault
const dataFileName = beansDataFileName


export const ShowBeans = () => {

    const [data, setData] = useState([])
    const [picArray, setPicArray] = useState([])
    const [settings, setSettings] = useState([])


    //let settings = []
    useEffect(()=>{
        const loadAllFileData = async () => {
            //load Settings
            let fileContent =  await readSettings(settingsFileName)
            console.log('filecontent:', fileContent)
            if (fileContent) {   //if filename doesn't exist readSettings will return false    
                //settings = JSON.parse(fileContent)
                setSettings(JSON.parse(fileContent))
            } else {
                //settings = settingsDefault
            }   setSettings(settingsDefault)
            // load data
            const rawData = await readData(dataFileName)
            const tempData = await createTable(rawData)
            console.log('tempdata type:', typeof tempData)
            setData(tempData)

            const folder = dataFileName.split('/')[0]
            /*
            for (let i=0; i<tempData.length; i++){
                const temp = await readPicture(folder + '/' + tempData[i].id.toString() + '.jpeg')
                console.log('temp',temp)
                picArray.push(temp)
            }
            setPicArray(picArray) */
            let tempArray = []
            let tempPics = {}
            for (let i=0; i<tempData.length; i++){
                const temp = await readPicture(folder + '/' + tempData[i].id.toString() + '.jpeg')
                tempPics[tempData[i].id]=temp
                console.log('tempData von i',tempData[i])
                console.log('tempPics:', tempPics)
                //console.log('temp',temp)
                //picArray.push(temp)
            }
            tempArray.push(tempPics)
            console.log('tempArray:', tempArray)
            setPicArray(tempArray)




            //const tempPics = await picData(tempData, dataFileName)
            //setPicArray(tempPics)

            //

        }
        loadAllFileData()
    }, [])



console.log('settings:', settings)
//const newArray = [...picArray]
let res2
if(picArray.length > 0 && data.length > 0){
    res2 = data.reverse().map((row, index1) => 
        <IonCard>

            {settings.filter(setting => setting[1] === true).map((setting, index2) =>
            <> 

            { (index2 === 0) ?
            <IonCardHeader> 
                <IonLabel color='dark'> <h1>{row[setting[0]]}</h1> </IonLabel>
                <IonLabel>{setting[0]}</IonLabel>
                <IonItem lines='none'>
                {picArray[0][row.id] ?
                    <IonImg style={{height: '200px'}} src={picArray[0][row.id]}/>
                    :
                    <IonImg style={{height: '50px'}} src= '../../../assets/icon/coffee-beans-icon.png'/>
                    }
                </IonItem>
            </IonCardHeader>

            : <>
                <IonCardContent>
                    {/*<IonItem class="ion-no-padding">*/}
                        <IonLabel color='dark'> <h2>{row[setting[0]]}</h2> </IonLabel>
                        <IonLabel>{setting[0]}</IonLabel>
                    {/*</IonItem>*/}
                </IonCardContent>
            </>}               
            </>)}
            <IonCardContent>   
                <IonLabel>{Object.keys(row)[1]}: {row[Object.keys(row)[1]]}</IonLabel>
            </IonCardContent>

        </IonCard>

    )
}

//{row[settings.filter(setting => setting[1] === true)[0][0]]}

/*
if(picArray.length > 0 && data.length > 0){
    res2 = data.map((pic, index) => 
    <>
    <IonItem>{pic.id} </IonItem> 
    <IonItem><IonImg src={picArray[0][pic.id] || newspaper}/> </IonItem>
    </>)
}*/



        //"http://192.168.1.38:8100/_capacitor_file_/storage/emulated/0/Android/data/io.ionic.starter/files/data//

    return (
        <IonPage>
            <IonHeader>
                <IonToolbar>
                        <IonButtons slot='start'>
                           <IonBackButton defaultHref='home'/>
                        </IonButtons>
                <IonTitle>Bohnen anzeigen</IonTitle>
                </IonToolbar>
                </IonHeader>
            <IonContent color='dark' style={{IonBackgroundColor:'#111D12'}}>
                <IonFab vertical='top' horizontal='center' slot='fixed'>
                    <IonFabButton color='dark' routerLink='./Add-beans'>
                        <IonIcon icon={add} />
                    </IonFabButton>
                </IonFab>
            <IonList>
                {res2}
            </IonList>
            </IonContent>
        </IonPage>
    )
}
